imports.line:
important;
}

.loss {
  color: #e74c3c !important;
}

/* History Actions */
.history-actions {
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
}

.history-actions button {
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 6px;
  font-size: 0.9rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.btn-primary {
  background-color: #3498db;
  color: white;
}

.btn-primary:hover {
  background-color: #2980b9;
}

.btn-secondary {
  background-color: #95a5a6;
  color: white;
}

.btn-secondary:hover {
  background-color: #7f8c8d;
}

.btn-danger {
  background-color: #e74c3c;
  color: white;
}

.btn-danger:hover {
  background-color: #c0392b;
}

/* Analysis Section */
.analysis-section {
  margin-bottom: 3rem;
}

.analysis-section h2 {
  font-size: 2rem;
  color: #2c3e50;
  margin-bottom: 2rem;
}

.analysis-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
}

.stats-card {
  background-color: white;
  border: 1px solid #ddd;
  border-radius: 12px;
  padding: 2rem;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.stats-card h3 {
  font-size: 1.4rem;
  color: #2c3e50;
  margin-bottom: 1.5rem;
  border-bottom: 2px solid #3498db;
  padding-bottom: 0.5rem;
}

/* Stats List */
.stats-list {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.stat-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.75rem 0;
  border-bottom: 1px solid #f0f0f0;
}

.stat-item:last-child {
  border-bottom: none;
}

.stat-item span:first-child {
  color: #7f8c8d;
  font-weight: 500;
}

.stat-item span:last-child {
  color: #2c3e50;
  font-weight: 600;
  font-size: 1.1rem;
}

/* Stakes Breakdown */
.stakes-breakdown {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.stakes-item {
  padding: 1rem;
  background-color: #f8f9fa;
  border-radius: 8px;
  border-left: 4px solid #3498db;
}

.stakes-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 0.5rem;
}

.stakes-name {
  font-weight: 600;
  color: #2c3e50;
}

.stakes-profit {
  font-weight: 600;
  font-size: 1.1rem;
}

.stakes-details {
  color: #7f8c8d;
  font-size: 0.9rem;
}

/* Hourly Breakdown */
.hourly-breakdown {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  max-height: 300px;
  overflow-y: auto;
}

.hourly-item {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 1rem;
  padding: 0.75rem;
  background-color: #f8f9fa;
  border-radius: 6px;
  align-items: center;
}

.hour {
  font-weight: 600;
  color: #2c3e50;
}

.hands {
  color: #7f8c8d;
  text-align: center;
}

/* Parsed Hands Section */
.parsed-hands-section {
  margin-bottom: 3rem;
}

.parsed-hands-section h2 {
  font-size: 2rem;
  color: #2c3e50;
  margin-bottom: 2rem;
}

/* Hands Table */
.hands-table {
  background-color: white;
  border: 1px solid #ddd;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.table-header {
  display: grid;
  grid-template-columns: 150px 180px 100px 80px 100px 100px 80px;
  gap: 1rem;
  padding: 1rem;
  background-color: #f8f9fa;
  font-weight: 600;
  color: #2c3e50;
  border-bottom: 2px solid #ddd;
}

.table-row {
  display: grid;
  grid-template-columns: 150px 180px 100px 80px 100px 100px 80px;
  gap: 1rem;
  padding: 1rem;
  border-bottom: 1px solid #f0f0f0;
  align-items: center;
  transition: background-color 0.2s ease;
}

.table-row:hover {
  background-color: #f8f9fa;
}

.table-row:last-child {
  border-bottom: none;
}

.hand-id {
  font-family: monospace;
  font-size: 0.9rem;
  color: #7f8c8d;
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 1rem;
  margin-top: 2rem;
  padding: 1rem;
}

.pagination button {
  padding: 0.5rem 1rem;
  border: 1px solid #ddd;
  background-color: white;
  color: #2c3e50;
  border-radius: 6px;
  cursor: pointer;
  font-weight: 500;
  transition: all 0.2s ease;
}

.pagination button:hover:not(:disabled) {
  background-color: #3498db;
  color: white;
  border-color: #3498db;
}

.pagination button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.pagination span {
  color: #7f8c8d;
  font-weight: 500;
}

/* Responsive Design */
@media (max-width: 768px) {
  .hand-history-analyzer {
    padding: 1rem;
  }

  .header h1 {
    font-size: 2rem;
  }

  .dropzone {
    padding: 2rem 1rem;
  }

  .hand-histories-grid {
    grid-template-columns: 1fr;
  }

  .analysis-grid {
    grid-template-columns: 1fr;
  }

  .table-header,
  .table-row {
    grid-template-columns: 1fr;
    gap: 0.5rem;
  }

  .table-header {
    display: none;
  }

  .table-row {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    padding: 1.5rem 1rem;
  }

  .table-row span {
    display: flex;
    justify-content: space-between;
    width: 100%;
    padding: 0.25rem 0;
  }

  .table-row span::before {
    content: attr(data-label);
    font-weight: 600;
    color: #7f8c8d;
  }

  .history-actions {
    flex-direction: column;
  }

  .history-actions button {
    width: 100%;
  }
}

@media (max-width: 480px) {
  .hand-history-analyzer {
    padding: 0.5rem;
  }

  .header {
    margin-bottom: 2rem;
  }

  .header h1 {
    font-size: 1.8rem;
  }

  .stats-card {
    padding: 1rem;
  }

  .hourly-item {
    grid-template-columns: 1fr;
    text-align: center;
  }
