  color: #adb5bd;
  font-size: 16px;
}

/* Bucket Grid */
.bucket-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: 20px;
  margin-bottom: 32px;
}

.bucket-card {
  background: #2d2d2d;
  border: 2px solid #444;
  border-radius: 12px;
  padding: 16px;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.bucket-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0,0,0,0.3);
}

.bucket-card.selected {
  border-color: var(--primary-color, #007bff);
  box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.25);
}

.bucket-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 12px;
}

.bucket-name {
  color: #e9ecef;
  font-size: 16px;
  font-weight: 600;
  margin: 0;
  flex-grow: 1;
}

.bucket-category-badge {
  color: white;
  font-size: 11px;
  font-weight: 600;
  padding: 4px 8px;
  border-radius: 12px;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Bucket Metrics */
.bucket-metrics {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 12px;
  margin-bottom: 16px;
}

.metric {
  text-align: center;
}

.metric-label {
  display: block;
  color: #adb5bd;
  font-size: 12px;
  font-weight: 500;
  margin-bottom: 4px;
}

.metric-value {
  display: block;
  font-size: 16px;
  font-weight: 700;
  color: #e9ecef;
}

.metric-value.positive {
  color: #28a745;
}

.metric-value.negative {
  color: #dc3545;
}

/* Strategy Preview Bar */
.bucket-strategy-preview {
  height: 24px;
}

.strategy-bar {
  display: flex;
  height: 100%;
  border-radius: 6px;
  overflow: hidden;
  background: #495057;
}

.strategy-segment {
  height: 100%;
  transition: all 0.3s ease;
}

.strategy-segment.fold {
  background: #dc3545;
}

.strategy-segment.check-call {
  background: #ffc107;
}

.strategy-segment.bet-raise {
  background: #28a745;
}

/* Bucket Details */
.bucket-details {
  background: #2d2d2d;
  border: 2px solid #444;
  border-radius: 12px;
  padding: 24px;
  margin-bottom: 32px;
}

.bucket-details h3 {
  color: #e9ecef;
  font-size: 24px;
  font-weight: 700;
  margin-bottom: 24px;
  text-align: center;
}

.details-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 24px;
}

.detail-section {
  background: #343a40;
  border-radius: 8px;
  padding: 20px;
}

.detail-section h4 {
  color: #e9ecef;
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 16px;
}

/* Strategy Breakdown */
.strategy-breakdown {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.strategy-item {
  display: flex;
  align-items: center;
  gap: 12px;
}

.strategy-label {
  color: #adb5bd;
  font-weight: 600;
  min-width: 80px;
  font-size: 14px;
}

.strategy-bar-detail {
  flex-grow: 1;
  height: 24px;
  background: #495057;
  border-radius: 12px;
  position: relative;
  overflow: hidden;
}

.strategy-fill {
  height: 100%;
  border-radius: 12px;
  transition: width 0.3s ease;
}

.strategy-fill.fold {
  background: #dc3545;
}

.strategy-fill.check-call {
  background: #ffc107;
}

.strategy-fill.bet-raise {
  background: #28a745;
}

.strategy-percentage {
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  color: #e9ecef;
  font-size: 12px;
  font-weight: 600;
}

/* EV Analysis */
.ev-analysis {
  text-align: center;
}

.ev-primary {
  margin-bottom: 8px;
}

.ev-value {
  color: #e9ecef;
  font-size: 32px;
  font-weight: 700;
}

.ev-unit {
  color: #adb5bd;
  font-size: 16px;
  margin-left: 4px;
}

.ev-context span {
  color: #adb5bd;
  font-size: 14px;
}

/* Frequency Analysis */
.frequency-analysis {
  text-align: center;
}

.frequency-circle {
  width: 80px;
  height: 80px;
  background: linear-gradient(135deg, #007bff, #0056b3);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 12px;
}

.frequency-value {
  color: white;
  font-size: 20px;
  font-weight: 700;
}

.frequency-context span {
  color: #adb5bd;
  font-size: 14px;
}

/* Nut Analysis */
.nut-analysis {
  text-align: center;
}

.nut-meter {
  height: 24px;
  background: #495057;
  border-radius: 12px;
  position: relative;
  margin-bottom: 12px;
  overflow: hidden;
}

.nut-fill {
  height: 100%;
  background: linear-gradient(90deg, #ffc107, #fd7e14);
  border-radius: 12px;
  transition: width 0.3s ease;
}

.nut-percentage {
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  color: #e9ecef;
  font-size: 12px;
  font-weight: 600;
}

.nut-context span {
  color: #adb5bd;
  font-size: 14px;
}

/* Nutability Analysis */
.nutability-analysis {
  background: #2d2d2d;
  border: 2px solid #444;
  border-radius: 12px;
  padding: 24px;
}

.nutability-analysis h3 {
  color: #e9ecef;
  font-size: 24px;
  font-weight: 700;
  margin-bottom: 24px;
  text-align: center;
}

.nutability-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 24px;
}

.nutability-section {
  background: #343a40;
  border-radius: 8px;
  padding: 20px;
}

.nutability-section h4 {
  color: #e9ecef;
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 16px;
}

/* Board Texture */
.board-texture {
  text-align: center;
}

.texture-type {
  display: block;
  color: #e9ecef;
  font-size: 20px;
  font-weight: 700;
  text-transform: capitalize;
  margin-bottom: 16px;
}

.nutability-score {
  display: flex;
  align-items: center;
  gap: 12px;
}

.score-meter {
  flex-grow: 1;
  height: 24px;
  background: #495057;
  border-radius: 12px;
  overflow: hidden;
}

.score-fill {
  height: 100%;
  background: linear-gradient(90deg, #dc3545, #ffc107, #28a745);
  border-radius: 12px;
  transition: width 0.3s ease;
}

.score-value {
  color: #e9ecef;
  font-size: 16px;
  font-weight: 700;
  min-width: 60px;
}

/* Nut Hands and Draws */
.nut-hands,
.nut-draws {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
}

.nut-hand-type,
.nut-draw-type {
  background: #495057;
  color: #e9ecef;
  padding: 6px 12px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: 600;
  text-transform: capitalize;
}

.nut-hand-type {
  background: linear-gradient(135deg, #6f42c1, #495057);
}

.nut-draw-type {
  background: linear-gradient(135deg, #fd7e14, #495057);
}

.no-draws {
  color: #6c757d;
  font-style: italic;
  font-size: 14px;
}

/* No Analysis State */
.no-analysis {
  text-align: center;
  padding: 60px 20px;
}

.no-analysis h3 {
  color: #e9ecef;
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 12px;
}

.no-analysis p {
  color: #adb5bd;
  font-size: 16px;
}

/* Responsive Design */
@media (max-width: 768px) {
  .bucket-analysis {
    padding: 16px;
  }

  .bucket-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .details-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .nutability-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .bucket-metrics {
    grid-template-columns: 1fr;
    gap: 8px;
  }

  .analysis-header h2 {
    font-size: 24px;
  }

  .bucket-details h3 {
    font-size: 20px;
  }
}

@media (max-width: 480px) {
  .strategy-item {
    flex-direction: column;
    align-items: stretch;
    gap: 6px;
  }

  .strategy-label {
    min-width: auto;
  }

  .nutability-score {
    flex-direction: column;
    gap: 8px;
