:root {
  /* Light theme colors */
  --bg-primary: #f5f5f5;
  --bg-secondary: #ffffff;
  --bg-tertiary: #f8f9fa;
  --text-primary: #333333;
  --text-secondary: #666666;
  --text-muted: #999999;
  --border-color: #e0e0e0;
  --border-light: #f0f0f0;
  --shadow-light: rgba(0, 0, 0, 0.1);
  --shadow-medium: rgba(0, 0, 0, 0.15);
  --shadow-heavy: rgba(0, 0, 0, 0.25);
  --primary-color: #ff5722;
  --primary-color-rgb: 255, 87, 34;
  --primary-dark: #d84315;
  --primary-light: rgba(255, 87, 34, 0.1);
  --accent-color: #ff9800;
  --accent-primary: #ff5722;
  --accent-secondary: #6c757d;
  --success-color: #28a745;
  --success-dark: #1e7e34;
  --success-light: rgba(40, 167, 69, 0.1);
  --warning-color: #ffc107;
  --warning-light: rgba(255, 193, 7, 0.1);
  --warning-hover: rgba(255, 193, 7, 0.2);
  --error-color: #dc3545;
  --danger-color: #dc3545;
  --danger-color-rgb: 220, 53, 69;
  --error-light: rgba(220, 53, 69, 0.1);
  --info-color: #17a2b8;
  --primary-hover: #d84315;
  --gradient-primary: linear-gradient(135deg, #ff5722 0%, #ff9800 100%);
  --gradient-secondary: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  --card-bg: #ffffff;
  --input-bg: #ffffff;
  --button-bg: #ffffff;
  --player-card-bg: #f5f5f5;
  --player-card-active: #e8f5e8;
  --player-card-folded: #f0f0f0;
  --player-card-allin: #fff3e0;
  --board-bg: #ffffff;
  --modal-bg: rgba(0, 0, 0, 0.5);

  /* Card color variables - will be overridden by JavaScript */
  --card-hearts-color: #dc3545;
  --card-diamonds-color: #007bff;
  --card-spades-color: #333333;
  --card-clubs-color: #28a745;
}

.dark-theme {
  /* Dark theme colors */
  --bg-primary: #0a0a0a;
  --bg-secondary: #1a1a1a;
  --bg-tertiary: #2a2a2a;
  --text-primary: #ffffff;
  --text-secondary: #cccccc;
  --text-muted: #888888;
  --border-color: #333333;
  --border-light: #2a2a2a;
  --shadow-light: rgba(0, 0, 0, 0.3);
  --shadow-medium: rgba(0, 0, 0, 0.4);
  --shadow-heavy: rgba(0, 0, 0, 0.6);
  --primary-color: #ff5722;
  --primary-color-rgb: 255, 87, 34;
  --primary-dark: #d84315;
  --accent-color: #ff9800;
  --accent-primary: #ff5722;
  --accent-secondary: #868e96;
  --success-color: #51cf66;
  --success-dark: #37b24d;
  --success-light: rgba(81, 207, 102, 0.1);
  --warning-color: #ffd43b;
  --warning-light: rgba(255, 212, 59, 0.1);
  --warning-hover: rgba(255, 212, 59, 0.2);
  --error-color: #ff6b6b;
  --error-light: rgba(255, 107, 107, 0.1);
  --info-color: #339af0;
  --primary-hover: #d84315;
  --gradient-primary: linear-gradient(135deg, #ff5722 0%, #ff9800 100%);
  --gradient-secondary: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  --card-bg: #1a1a1a;
  --input-bg: #2a2a2a;
  --button-bg: #2a2a2a;
  --player-card-bg: #1a1a1a;
  --player-card-active: #1e3a1e;
  --player-card-folded: #2a2a2a;
  --player-card-allin: #3d2f1a;
  --board-bg: #1a1a1a;
  --modal-bg: rgba(0, 0, 0, 0.8);
}

/* Global theme transition */
* {
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;
}

/* Mobile-specific improvements */
@media (max-width: 768px) {
  /* Prevent horizontal overflow */
  html, body {
    overflow-x: hidden;
    max-width: 100vw;
  }

  * {
    box-sizing: border-box;
  }

  /* Ensure containers don't overflow */
  .nav-container,
  .hero-container,
  .section-container,
  .app-nav,
  .app-content {
    max-width: 100vw;
    overflow-x: hidden;
  }

  /* Improve tap targets for mobile */
  button, 
  .card, 
  .card-selector, 
  .nav-link, 
  .mode-button {
    min-height: 44px; /* iOS recommended touch target */
    min-width: 44px;
  }

  /* Prevent zoom on input focus for iOS */
  input, 
  select, 
  textarea {
    font-size: 16px !important;
  }

  /* Improve touch scrolling */
  .card-picker-modal,
  .modal-content,
  .nav-center {
    -webkit-overflow-scrolling: touch;
  }

  /* Fix flex layouts that might overflow */
  .hero-stats,
  .nav-actions,
  .card-input-row,
  .community-buttons,
  .opponent-buttons,
  .bet-input-buttons,
  .action-buttons,
  .mode-toggle {
    flex-wrap: wrap;
  }

  /* Ensure proper text wrapping */
  .hero-title,
  .hero-subtitle,
  .spot-section h3,
  .spot-section h4,
  .player-name,
  .nav-link {
    word-wrap: break-word;
    word-break: break-word;
  }

  /* Fix grid layouts for mobile */
  .hero-container,
  .features-grid,
  .plans-grid,
  .faq-grid {
    grid-template-columns: 1fr !important;
  }

  /* Better spacing for mobile fingers */
  .card-picker-grid {
    gap: 1rem;
    grid-template-columns: repeat(auto-fit, minmax(60px, 1fr));
  }

  .card-picker-card {
    padding: 1rem 0.5rem;
    font-size: 1rem;
  }

  /* Stack layout for better mobile UX */
  .spot-mode-interface {
    padding: 1rem 0.5rem;
    max-width: 100vw;
    overflow-x: hidden;
  }

  .card-input-row {
    flex-direction: column;
    align-items: stretch;
    gap: 1rem;
  }

  .card-input {
    width: 100%;
    max-width: 100%;
  }

  .card-selector {
    width: 100%;
    padding: 1rem;
    font-size: 1rem;
    text-align: center;
    max-width: 100%;
  }

  /* Better button layout */
  .community-buttons,
  .opponent-buttons {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
  }

  .randomize-btn,
  .clear-btn,
  .add-opponent-btn {
    width: 100%;
    padding: 1rem;
    font-size: 1rem;
    max-width: 100%;
  }

  /* Ensure cards don't overflow */
  .cards,
  .board-cards {
    flex-wrap: wrap;
    justify-content: center;
    max-width: 100%;
  }

  .card {
    flex-shrink: 0;
    max-width: calc(20% - 0.5rem);
    min-width: 50px;
  }

  /* Fix player cards layout */
  .players {
    grid-template-columns: 1fr;
    gap: 1rem;
    padding: 0.5rem;
    max-width: 100vw;
    overflow-x: hidden;
  }

  .player-card {
    width: 100%;
    max-width: 100%;
    padding: 1rem;
    margin: 0;
    overflow-x: hidden;
  }

  /* Larger text for readability */
  .spot-section h3 {
    font-size: 1.25rem;
  }

  .spot-section h4 {
    font-size: 1.1rem;
  }

  /* Better spacing for mobile */
  .spot-section {
    margin-bottom: 1.5rem;
    padding: 1.5rem 1rem;
    max-width: 100%;
    overflow-x: hidden;
  }

  /* Fix action buttons overflow */
  .action-buttons {
    flex-direction: column;
    gap: 0.75rem;
    width: 100%;
  }

  .action-btn {
    width: 100%;
    padding: 1rem;
    font-size: 1rem;
    max-width: 100%;
  }

  /* Fix equity section overflow */
  .equity-section {
    max-width: 100%;
    overflow-x: hidden;
  }

  .equity-row {
    flex-direction: column;
    gap: 0.5rem;
    text-align: center;
  }

  .equity-box {
    width: 100%;
    max-width: 100%;
    padding: 0.75rem;
    font-size: 1rem;
  }

  /* Fix modal overflow */
  .modal-content {
    margin: 1rem;
    max-width: calc(100vw - 2rem);
    max-height: calc(100vh - 2rem);
    overflow-y: auto;
    overflow-x: hidden;
  }

  /* Fix navigation overflow */
  .app-nav {
    flex-wrap: wrap;
    padding: 1rem;
  }

  .nav-left,
  .nav-center,
  .nav-right {
    max-width: 100%;
  }
}

body {
  background-color: var(--bg-primary);
  color: var(--text-primary);
  margin: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', sans-serif;
  line-height: 1.6;
  overflow-x: hidden;
  max-width: 100vw;
  /* Ensure smooth scrolling for sticky header */
  scroll-behavior: smooth;
}

/* Global overflow prevention */
* {
  box-sizing: border-box;
}

/* Universal mobile overflow fixes */
.app,
.app-wrapper,
.landing-page,
.spot-mode-interface,
.modal-content,
.player-card,
.spot-section,
.equity-section,
.board-container {
  max-width: 100vw;
  overflow-x: hidden;
}

/* Ensure all flex containers wrap properly */
.nav-actions,
.hero-actions,
.hero-stats,
.cards,
.board-cards,
.action-buttons,
.card-input-row,
.community-buttons,
.opponent-buttons,
.bet-input-buttons,
.mode-toggle,
.features-grid,
.plans-grid {
  flex-wrap: wrap;
  max-width: 100%;
}

/* Text wrapping for long content */
h1, h2, h3, h4, h5, h6,
.hero-title,
.hero-subtitle,
.player-name,
.spot-section h3,
.spot-section h4 {
  word-wrap: break-word;
  word-break: break-word;
  max-width: 100%;
}

/* LANDING PAGE STYLES */
.landing-page {
  min-height: 100vh;
  background-color: var(--bg-primary);
  color: var(--text-primary);
}

/* Navigation */
.landing-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background-color: rgba(10, 10, 10, 0.95);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--border-color);
  z-index: 1000;
  padding: 0;
}

.nav-container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
}

.nav-brand {
  display: flex;
  align-items: center;
}

.brand-logo {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  text-decoration: none;
  color: var(--text-primary);
}

.brand-text {
  font-size: 1.5rem;
  font-weight: 700;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.nav-actions {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.nav-login-btn {
  background: transparent;
  border: 1px solid var(--border-color);
  color: var(--text-secondary);
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.nav-login-btn:hover {
  border-color: var(--primary-color);
  color: var(--primary-color);
}

.nav-cta-btn {
  background: var(--gradient-primary);
  border: none;
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  font-size: 0.9rem;
  font-weight: 500;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.3s ease;
}

.nav-cta-btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 20px rgba(255, 87, 34, 0.3);
}

/* Hero Section */
.hero-section {
  padding: 8rem 2rem 4rem;
  background: var(--bg-primary);
  position: relative;
  overflow: hidden;
}

.hero-container {
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.hero-content {
  z-index: 2;
}

.hero-title {
  font-size: 3.5rem;
  font-weight: 800;
  line-height: 1.2;
  margin-bottom: 1.5rem;
  color: var(--text-primary);
}

.hero-highlight {
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.hero-subtitle {
  font-size: 1.25rem;
  color: var(--text-secondary);
  margin-bottom: 2rem;
  line-height: 1.6;
}

.hero-actions {
  display: flex;
  gap: 1rem;
  margin-bottom: 3rem;
}

.hero-cta-primary {
  background: var(--gradient-primary);
  border: none;
  color: white;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  display: inline-block;
  transition: all 0.3s ease;
}

.hero-cta-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 30px rgba(255, 87, 34, 0.4);
}

.hero-cta-secondary {
  background: transparent;
  border: 2px solid var(--border-color);
  color: var(--text-primary);
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  display: inline-block;
  transition: all 0.3s ease;
}

.hero-cta-secondary:hover {
  border-color: var(--primary-color);
  color: var(--primary-color);
  transform: translateY(-2px);
}

.hero-stats {
  display: flex;
  gap: 2rem;
  flex-wrap: wrap;
  justify-content: center;
}

.stat-item {
  text-align: center;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', sans-serif;
}

.stat-number {
  display: block;
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--primary-color);
  margin-bottom: 0.25rem;
  font-family: inherit;
  letter-spacing: -0.025em;
  line-height: 1;
}

.stat-label {
  display: block;
  font-size: 0.9rem;
  color: var(--text-muted);
  font-family: inherit;
  font-weight: 500;
  line-height: 1.3;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Hero Visual */
.hero-visual {
  display: flex;
  justify-content: center;
  align-items: center;
}

.hero-app-preview {
  max-width: 500px;
  width: 100%;
}

.preview-window {
  background: var(--bg-secondary);
  border-radius: 1rem;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  overflow: hidden;
  border: 1px solid var(--border-color);
}

.window-header {
  background: var(--bg-tertiary);
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  border-bottom: 1px solid var(--border-color);
}

.window-controls {
  display: flex;
  gap: 0.5rem;
}

.control-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
}

.control-dot.red { background: #ff5f56; }
.control-dot.yellow { background: #ffbd2e; }
.control-dot.green { background: #27ca3f; }

.window-title {
  font-size: 0.9rem;
  color: var(--text-secondary);
  font-weight: 500;
}

.preview-content {
  padding: 2rem;
}

.preview-cards-section {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 2rem;
}

.hero-hand,
.opponent-hand {
  display: flex;
  gap: 0.5rem;
}

.hero-card {
  width: 40px;
  height: 56px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.9rem;
  font-weight: 600;
  border: 1px solid var(--border-color);
  background: white;
  color: #333;
}

.hero-card.hearts { color: #dc3545; }
.hero-card.diamonds { color: #007bff; }
.hero-card.spades { color: #333; }
.hero-card.clubs { color: #28a745; }

.hero-card.back {
  background: var(--gradient-primary);
  color: white;
  font-size: 1.2rem;
}

.vs-indicator {
  font-size: 1.2rem;
  font-weight: 700;
  color: var(--text-muted);
}

.board-section {
  margin-bottom: 2rem;
}

.board-label {
  font-size: 0.9rem;
  color: var(--text-muted);
  margin-bottom: 0.5rem;
}

.board-cards {
  display: flex;
  flex-direction: row;
  gap: 0.5rem;
  align-items: center;
  justify-content: center;
}

.equity-display {
  margin-top: 2rem;
}

.equity-bar {
  width: 100%;
  height: 30px;
  background: var(--bg-tertiary);
  border-radius: 15px;
  overflow: hidden;
  position: relative;
  border: 1px solid var(--border-color);
}

.equity-fill {
  height: 100%;
  background: var(--gradient-primary);
  transition: width 0.3s ease;
}

.equity-text {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 0.9rem;
  font-weight: 600;
  color: white;
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.3);
}

/* Features Section */
.features-section {
  padding: 6rem 2rem;
  background: var(--bg-secondary);
}

.section-container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-header {
  text-align: center;
  margin-bottom: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
}

.section-header h2 {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: var(--text-primary);
  display: block;
  white-space: normal;
  line-height: 1.2;
}

.section-header p {
  font-size: 1.2rem;
  color: var(--text-secondary);
  max-width: 600px;
  margin: 0 auto;
  display: block;
  white-space: normal;
  line-height: 1.6;
}

.features-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
}

.feature-card {
  background: var(--bg-primary);
  padding: 2rem;
  border-radius: 1rem;
  border: 1px solid var(--border-color);
  transition: all 0.3s ease;
}

.feature-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  border-color: var(--primary-color);
}

.feature-icon {
  font-size: 2.5rem;
  margin-bottom: 1rem;
}

.feature-card h3 {
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.feature-card p {
  color: var(--text-secondary);
  line-height: 1.6;
}

/* Practice Section */
.practice-section {
  padding: 6rem 2rem;
  background: var(--bg-primary);
}

.practice-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
}

.practice-text h2 {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.practice-text p {
  font-size: 1.2rem;
  color: var(--text-secondary);
  margin-bottom: 2rem;
}

.practice-features {
  margin-bottom: 2rem;
}

.practice-feature {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 1rem;
}

.checkmark {
  color: var(--success-color);
  font-weight: 600;
  font-size: 1.1rem;
}

.practice-cta {
  background: var(--gradient-primary);
  border: none;
  color: white;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
}

.practice-cta:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 30px rgba(255, 87, 34, 0.4);
}

/* Mobile Preview */
.mobile-preview {
  display: flex;
  justify-content: center;
}

.mobile-frame {
  width: 280px;
  height: 500px;
  background: #1a1a1a;
  border-radius: 2rem;
  padding: 1rem;
  border: 3px solid #333;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.mobile-screen {
  width: 100%;
  height: 100%;
  background: var(--bg-secondary);
  border-radius: 1.5rem;
  overflow: hidden;
  border: 1px solid var(--border-color);
}

.mobile-content {
  padding: 2rem 1.5rem;
  text-align: center;
}

.mobile-header {
  font-size: 1.1rem;
  font-weight: 600;
  margin-bottom: 2rem;
  color: var(--text-primary);
}

.mobile-cards {
  display: flex;
  justify-content: center;
  gap: 0.25rem;
  margin-bottom: 2rem;
}

.mini-card {
  width: 28px;
  height: 40px;
  background: white;
  border-radius: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.7rem;
  font-weight: 600;
  color: #333;
  border: 1px solid #ddd;
}

.mobile-question {
  font-size: 1rem;
  margin-bottom: 2rem;
  color: var(--text-primary);
}

.mobile-actions {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.mobile-btn {
  background: var(--button-bg);
  border: 1px solid var(--border-color);
  color: var(--text-primary);
  padding: 0.75rem;
  border-radius: 0.5rem;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.mobile-btn:hover {
  border-color: var(--primary-color);
  background: var(--primary-light);
}

/* CTA Section */
.cta-section {
  padding: 6rem 2rem;
  background: var(--bg-secondary);
  text-align: center;
}

.cta-content h2 {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.cta-content p {
  font-size: 1.2rem;
  color: var(--text-secondary);
  margin-bottom: 2rem;
}

.cta-actions {
  display: flex;
  gap: 1rem;
  justify-content: center;
}

.cta-primary {
  background: var(--gradient-primary);
  border: none;
  color: white;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  display: inline-block;
  transition: all 0.3s ease;
}

.cta-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 30px rgba(255, 87, 34, 0.4);
}

.cta-secondary {
  background: transparent;
  border: 2px solid var(--border-color);
  color: var(--text-primary);
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  display: inline-block;
  transition: all 0.3s ease;
}

.cta-secondary:hover {
  border-color: var(--primary-color);
  color: var(--primary-color);
  transform: translateY(-2px);
}

/* Footer */
.landing-footer {
  background: var(--bg-primary);
  border-top: 1px solid var(--border-color);
  padding: 3rem 2rem 1rem;
}

.footer-container {
  max-width: 1200px;
  margin: 0 auto;
}

.footer-content {
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 4rem;
  margin-bottom: 2rem;
}

.footer-brand {
  max-width: 400px;
}

.footer-logo {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 1rem;
}

.footer-logo span {
  font-size: 1.25rem;
  font-weight: 700;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.footer-brand p {
  color: var(--text-secondary);
  line-height: 1.6;
}

.footer-links {
  display: flex;
  gap: 3rem;
}

.footer-column h4 {
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.footer-column a,
.footer-column button {
  display: block;
  color: var(--text-secondary);
  text-decoration: none;
  margin-bottom: 0.5rem;
  transition: color 0.3s ease;
  background: none;
  border: none;
  padding: 0;
  cursor: pointer;
  text-align: left;
}

.footer-column a:hover,
.footer-column button:hover {
  color: var(--primary-color);
}

.footer-bottom {
  border-top: 1px solid var(--border-color);
  padding-top: 2rem;
  text-align: center;
}

.footer-bottom p {
  color: var(--text-muted);
  margin: 0;
}

/* Beta Modal Updates */
.beta-modal {
  max-width: 500px;
  width: 90%;
}

.beta-signup-form h2 {
  color: var(--text-primary);
  margin-bottom: 1rem;
}

.beta-signup-form p {
  color: var(--text-secondary);
  margin-bottom: 2rem;
  line-height: 1.6;
}

.beta-signup-btn {
  background: var(--gradient-primary);
  border: none;
  color: white;
  padding: 1rem 2rem;
  border-radius: 0.75rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  width: 100%;
  transition: all 0.3s ease;
}

.beta-signup-btn:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 8px 30px rgba(255, 87, 34, 0.4);
}

.beta-signup-btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  transform: none;
}

/* Responsive Design */
/* Medium-large screens - ensure proper hero layout for tablets */
@media (max-width: 900px) {
  .hero-container {
    grid-template-columns: 1fr;
    gap: 3rem;
    text-align: center;
  }

  .hero-title {
    max-width: 100%;
    width: 100%;
    word-wrap: break-word;
    word-break: normal;
    line-height: 1.3;
  }

  .hero-subtitle {
    max-width: 100%;
    width: 100%;
  }

  .hero-stats {
    flex-direction: column;
    gap: 1.5rem;
    align-items: center;
  }
}

/* Specific fix for 780-820px range - tablet landscape */
@media (max-width: 820px) and (min-width: 769px) {
  .hero-container {
    grid-template-columns: 1fr;
    gap: 2.5rem;
    text-align: center;
    padding: 0 2rem;
  }

  .hero-title {
    font-size: 2.8rem;
    max-width: 100%;
    width: 100%;
    word-wrap: break-word;
    word-break: normal;
    line-height: 1.25;
    margin-bottom: 1.5rem;
  }

  .hero-subtitle {
    font-size: 1.15rem;
    max-width: 100%;
    width: 100%;
    margin-bottom: 2rem;
  }

  .hero-actions {
