/* Spot Mode Styles */
.mode-toggle {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
  justify-content: center;
}

.mode-button {
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  background-color: #2c3e50;
  color: white;
  cursor: pointer;
  transition: all 0.3s ease;
}

.mode-button:hover {
  background-color: #34495e;
}

.mode-button.active {
