  background: var(--card-bg);
  color: var(--text-primary);
  text-align: left;
  cursor: pointer;
  font-size: 14px;
  transition: background-color 0.2s ease;
  display: flex;
  align-items: center;
  gap: 8px;
}

.user-menu-item:hover {
