.toggle-switch {
  width: 60px;
  height: 30px;
  background: rgba(255, 255, 255, 0.3);
  border: none;
  border-radius: 15px;
  position: relative;
  cursor: pointer;
  transition: background 0.3s ease;
}

.toggle-slider {
  width: 26px;
  height: 26px;
  background: white;
  border-radius: 50%;
  position: absolute;
  top: 2px;
  transition: transform 0.3s ease;
}

.toggle-slider.monthly {
  transform: translateX(2px);
}

.toggle-slider.yearly {
