/* App Navigation Styles */
.app-nav {
  background: var(--bg-primary);
  border-bottom: 1px solid var(--border-color);
  padding: 15px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: -webkit-sticky; /* Safari support */
  position: sticky;
  top: 0;
  z-index: 1000; /* Increased z-index to ensure it stays above content */
  box-shadow: 0 2px 4px var(--shadow-light);
  transition: box-shadow 0.3s ease, backdrop-filter 0.3s ease;
  backdrop-filter: blur(10px); /* Add backdrop blur for better readability over content */
  -webkit-backdrop-filter: blur(10px); /* Safari support */
}

/* Enhanced shadow when content scrolls behind */
.app-nav.scrolled {
  box-shadow: 0 4px 12px var(--shadow-medium);
}

.nav-left, .nav-right {
  flex: 1;
}

.nav-center {
  flex: 2;
  display: flex;
  justify-content: center;
}

.nav-right {
  display: flex;
  justify-content: flex-end;
}

.nav-home-btn {
  background: none;
  border: none;
  color: var(--text-secondary);
  font-size: 1rem;
  cursor: pointer;
  padding: 8px 12px;
  border-radius: 6px;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.nav-home-btn .brand-text {
  font-size: 1.25rem;
  font-weight: 600;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.nav-home-btn:hover {
  background: var(--bg-tertiary);
  color: var(--text-primary);
}

.nav-back-btn {
  background: none;
  border: none;
  color: var(--text-secondary);
  font-size: 1.2rem;
  cursor: pointer;
  padding: 8px 12px;
  border-radius: 6px;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 44px;
  height: 44px;
}

.nav-back-btn:hover {
  background: var(--bg-tertiary);
  color: var(--primary-color);
  transform: translateX(-2px);
}

.app-content {
  padding: 20px;
}

/* Mobile Header Styles */
.app-header {
  background-color: var(--bg-secondary);
  border-bottom: 1px solid var(--border-color);
  position: sticky;
  top: 0;
  z-index: 1000;
}

.header-container {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  position: relative;
}

.header-brand .brand-logo {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  text-decoration: none;
  color: var(--text-primary);
  font-weight: 600;
}

.brand-text {
  font-size: 1.25rem;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.mobile-menu-toggle {
  display: none;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0.5rem;
  z-index: 1001;
}

.hamburger {
  display: flex;
  flex-direction: column;
  width: 24px;
  height: 18px;
  position: relative;
}

.hamburger span {
  display: block;
  height: 2px;
  width: 100%;
  background-color: var(--text-primary);
  margin-bottom: 4px;
  transition: all 0.3s ease;
}

.hamburger span:last-child {
  margin-bottom: 0;
}

.hamburger.open span:nth-child(1) {
  transform: rotate(45deg) translate(5px, 5px);
}

.hamburger.open span:nth-child(2) {
  opacity: 0;
}

.hamburger.open span:nth-child(3) {
  transform: rotate(-45deg) translate(7px, -6px);
}

.header-nav {
  display: flex;
  align-items: center;
  gap: 2rem;
}

.nav-links {
  display: flex;
  gap: 1.5rem;
}

.nav-link {
  color: var(--text-secondary);
  text-decoration: none;
  padding: 0.5rem 1rem;
  border-radius: 0.5rem;
  transition: all 0.3s ease;
  font-weight: 500;
}

.nav-link:hover {
  color: var(--text-primary);
  background-color: var(--bg-tertiary);
}

.nav-link.active {
  color: var(--primary-color);
  background-color: var(--primary-light);
}

.header-actions {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.user-section {
  display: flex;
  align-items: center;
  gap: 1rem;
}

.user-name {
  color: var(--text-secondary);
  font-size: 0.9rem;
}

.logout-btn {
  padding: 0.5rem 1rem;
  background: transparent;
  border: 1px solid var(--border-color);
  color: var(--text-secondary);
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
}

.logout-btn:hover {
  border-color: var(--primary-color);
  color: var(--primary-color);
}

.cta-btn {
  padding: 0.5rem 1rem;
  background: var(--gradient-primary);
  color: white;
  text-decoration: none;
  border-radius: 0.5rem;
  font-weight: 500;
  transition: all 0.3s ease;
}

.cta-btn:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 20px rgba(255, 87, 34, 0.3);
}

.mobile-menu-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5);
  z-index: 999;
  display: none;
}

/* Responsive Design */
@media (max-width: 768px) {
  .hero-section {
    flex-direction: column;
    text-align: center;
    padding: 60px 20px;
  }

  .hero-visual {
    padding-left: 0;
    margin-top: 40px;
  }

  .hero-title {
    font-size: 2.5rem;
  }

  .app-preview {
    width: 300px;
    transform: none;
  }

  .features-grid {
    grid-template-columns: 1fr;
  }

  .plans-grid {
    grid-template-columns: 1fr;
  }

  .pricing-card.popular {
    transform: none;
  }

  .faq-grid {
    grid-template-columns: 1fr;
  }

  .app-nav {
    flex-direction: column;
    gap: 15px;
    position: -webkit-sticky; /* Safari support */
    position: sticky;
    top: 0;
    z-index: 1000;
  }

  .nav-left, .nav-center, .nav-right {
    flex: none;
  }

  .nav-left {
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
  }

  .mobile-menu-toggle {
    display: block !important;
  }

  .nav-center {
    position: fixed;
    top: 0;
    left: -100%;
    width: 280px;
    height: 100vh;
    background-color: var(--bg-secondary);
    flex-direction: column;
    align-items: flex-start;
    padding: 6rem 2rem 2rem 2rem;
    transition: left 0.3s ease;
    box-shadow: 2px 0 10px rgba(0, 0, 0, 0.1);
    z-index: 1000;
    overflow-y: auto;
  }

  .nav-center.mobile-open {
    left: 0;
  }

  .mode-toggle {
    flex-direction: column;
    gap: 1rem;
    width: 100%;
  }

  .mode-button {
    width: 100%;
    padding: 1rem;
    text-align: left;
    border-radius: 0.5rem;
    font-size: 1rem;
    justify-content: flex-start;
  }

  .nav-right {
    flex-direction: column;
    gap: 1rem;
    width: 100%;
    margin-top: 1rem;
  }

  .auth-button, .user-button {
    width: 100%;
    padding: 1rem;
    text-align: center;
  }

  .mobile-menu-overlay {
    display: block !important;
  }
}

