}

.toggle-folded-btn.reactivate {
  background-color: #28a745;
  color: white;
}

.toggle-folded-btn.reactivate:hover:not(:disabled) {
  background-color: #218838;
}

.toggle-folded-btn:disabled {
  background-color: #6c757d;
