/* Folded State Styling */
.folded-state {
  background-color: #f8f9fa !important;
  border: 2px dashed #dc3545 !important;
  opacity: 0.7;
  position: relative;
}

.folded-state::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: repeating-linear-gradient(
    45deg,
    transparent,
    transparent 10px,
    rgba(220, 53, 69, 0.1) 10px,
    rgba(220, 53, 69, 0.1) 20px
  );
  pointer-events: none;
  border-radius: inherit;
}

.folded-state .card-selector:disabled {
