  align-items: center;
  justify-content: center;
  z-index: 10000;
}

.card-picker {
  background: var(--card-bg);
  border-radius: 10px;
  padding: 24px 24px 16px 24px;
  box-shadow: 0 8px 32px var(--shadow-heavy);
