}

.card-picker-grid {
  display: grid;
  grid-template-columns: repeat(8, 1fr);
  gap: 8px;
  margin-bottom: 18px;
}

.card-picker-card {
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: 40px;
  min-height: 56px;
  border-radius: 4px;
  background: var(--bg-tertiary);
  border: 2px solid var(--border-color);
  font-size: 16px;
  font-weight: bold;
  transition: background 0.2s, border 0.2s, transform 0.1s;
}

.card-picker-card:hover {
