  margin-top: 10px;
}

.facebook-button:hover {
  background-color: #166fe5;
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(24, 119, 242, 0.3);
}

.auth-footer {
  text-align: center;
  padding-top: 20px;
  border-top: 1px solid #e1e5e9;
}

.auth-footer p {
  color: #666;
  font-size: 14px;
  margin: 0;
}

.link-button {
  background: none;
  border: none;
  color: #4CAF50;
  font-weight: 600;
  cursor: pointer;
  text-decoration: underline;
  font-size: 14px;
}

.link-button:hover:not(:disabled) {
  color: #45a049;
}

.link-button:disabled {
  color: #6c757d;
  cursor: not-allowed;
}

/* Responsive Design for Modal */
@media (max-width: 768px) {
  .modal-overlay {
    padding: 10px;
  }

  .modal-content {
    max-width: 100%;
    margin: 0;
  }

  .auth-card {
    padding: 30px 20px 20px;
  }

  .auth-header h2 {
    font-size: 24px;
  }

  .form-row {
    flex-direction: column;
