  gap: 4px;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  border: 1px solid;
}

.tier-badge.green {
  background-color: rgba(76, 175, 80, 0.1);
  color: #4CAF50;
  border-color: #4CAF50;
}

.tier-badge.blue {
  background-color: rgba(33, 150, 243, 0.1);
  color: #2196F3;
  border-color: #2196F3;
}

.tier-badge.purple {
  background-color: rgba(156, 39, 176, 0.1);
  color: #9C27B0;
  border-color: #9C27B0;
}

.tier-icon {
  font-size: 12px;
}

.tier-name {
  font-size: 11px;
}

.upgrade-button {
  background: linear-gradient(135deg, #FF6B6B, #FF8E53);
  color: white;
  border: none;
  padding: 4px 8px;
  border-radius: 8px;
  font-size: 10px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.upgrade-button:hover {
  transform: translateY(-1px);
  box-shadow: 0 2px 8px rgba(255, 107, 107, 0.3);
}

/* Dark theme adjustments */
[data-theme="dark"] .tier-badge.green {
  background-color: rgba(76, 175, 80, 0.2);
  color: #81C784;
}

[data-theme="dark"] .tier-badge.blue {
  background-color: rgba(33, 150, 243, 0.2);
  color: #64B5F6;
}

[data-theme="dark"] .tier-badge.purple {
  background-color: rgba(156, 39, 176, 0.2);
  color: #BA68C8;
}

/* Player Profiles Styles */
.player-profiles-page {
  padding: 20px;
  max-width: 1400px;
  margin: 0 auto;
}

.profiles-header {
  text-align: center;
  margin-bottom: 30px;
}

.profiles-header h1 {
  color: var(--text-primary);
  margin-bottom: 10px;
}

.profiles-header p {
  color: var(--text-secondary);
  margin-bottom: 20px;
}

.create-profile-btn {
  background: linear-gradient(135deg, #4CAF50, #45a049);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
}

.create-profile-btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(76, 175, 80, 0.3);
}

.profiles-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 30px;
}

.profiles-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
}

.profile-card {
  background: var(--bg-secondary);
  border: 2px solid var(--border-color);
  border-radius: 12px;
  padding: 20px;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
}

.profile-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
}

.profile-card.selected {
  border-color: #2196F3;
  box-shadow: 0 0 20px rgba(33, 150, 243, 0.3);
}

.profile-card.predefined {
  border-left: 4px solid #4CAF50;
}

.profile-card.custom {
  border-left: 4px solid #FF9800;
}

.profile-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
}

.profile-header h3 {
  color: var(--text-primary);
  margin: 0;
}

.profile-type-badge {
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 12px;
  font-weight: 600;
  text-transform: uppercase;
}

.profile-type-badge.predefined {
  background: rgba(76, 175, 80, 0.1);
  color: #4CAF50;
}

.profile-type-badge.custom {
  background: rgba(255, 152, 0, 0.1);
  color: #FF9800;
}

.profile-description {
  color: var(--text-secondary);
  font-size: 14px;
  margin-bottom: 20px;
  line-height: 1.4;
}

.profile-stats {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.stat-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 13px;
}

.stat-row span:first-child {
  color: var(--text-secondary);
  min-width: 80px;
}

.stat-bar {
  flex: 1;
  height: 6px;
  background: var(--border-color);
  border-radius: 3px;
  margin: 0 10px;
  position: relative;
  overflow: hidden;
}

.stat-fill {
  height: 100%;
  border-radius: 3px;
  transition: width 0.3s ease;
}

.stat-fill.tightness {
  background: linear-gradient(90deg, #4CAF50, #8BC34A);
}

.stat-fill.aggression {
  background: linear-gradient(90deg, #FF5722, #FF9800);
}

.stat-fill.bluff {
  background: linear-gradient(90deg, #9C27B0, #E91E63);
}

.stat-value {
  color: var(--text-primary);
  font-weight: 600;
  min-width: 35px;
  text-align: right;
}

.delete-profile-btn {
  position: absolute;
  top: 10px;
  right: 10px;
  background: #f44336;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 4px 8px;
  font-size: 12px;
  cursor: pointer;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.profile-card:hover .delete-profile-btn {
  opacity: 1;
}

.profile-details-panel {
  background: var(--bg-secondary);
  border-radius: 12px;
  padding: 25px;
  height: fit-content;
  max-height: 80vh;
  overflow-y: auto;
}

.profile-details h2 {
  color: var(--text-primary);
  margin-bottom: 15px;
}

.details-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 25px;
  margin: 25px 0;
}

.detail-section {
  background: var(--bg-tertiary);
  padding: 20px;
  border-radius: 8px;
}

.detail-section h3 {
  color: var(--text-primary);
  margin-bottom: 15px;
  font-size: 16px;
  border-bottom: 2px solid var(--border-color);
  padding-bottom: 8px;
}

.detail-item {
  display: flex;
  justify-content: space-between;
  margin-bottom: 10px;
  font-size: 14px;
}

.detail-item span:first-child {
  color: var(--text-secondary);
}

.detail-item span:last-child {
  color: var(--text-primary);
  font-weight: 600;
}

.exploits-section {
  background: var(--bg-tertiary);
  padding: 20px;
  border-radius: 8px;
  margin-top: 25px;
}

.exploits-section h3 {
  color: var(--text-primary);
  margin-bottom: 15px;
}

.exploit-list {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.exploit-item {
  background: var(--bg-secondary);
  padding: 12px;
  border-radius: 6px;
  border-left: 4px solid #2196F3;
}

.exploit-type {
  font-weight: 600;
  color: #2196F3;
  display: block;
  margin-bottom: 4px;
}

.create-profile-modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
}

.modal-content {
  background: var(--bg-secondary);
  padding: 30px;
  border-radius: 12px;
  width: 90%;
  max-width: 600px;
  max-height: 90vh;
  overflow-y: auto;
}

.modal-content h2 {
  color: var(--text-primary);
  margin-bottom: 25px;
  text-align: center;
}

.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  color: var(--text-primary);
  font-weight: 600;
}

.form-group input,
.form-group textarea {
  width: 100%;
  padding: 12px;
  border: 2px solid var(--border-color);
  border-radius: 6px;
  background: var(--bg-primary);
  color: var(--text-primary);
  font-size: 14px;
}

.form-group input[type="range"] {
  width: calc(100% - 50px);
  margin-right: 10px;
}

.form-group input[type="range"] + span {
  display: inline-block;
  min-width: 40px;
  text-align: center;
  font-weight: 600;
  color: var(--text-primary);
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
}

.form-actions {
  display: flex;
  gap: 15px;
  justify-content: center;
  margin-top: 30px;
}

.form-actions button {
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
}

.form-actions button[type="submit"] {
  background: #4CAF50;
  color: white;
}

.form-actions button[type="submit"]:hover {
  background: #45a049;
}

.form-actions button[type="button"] {
  background: var(--border-color);
  color: var(--text-primary);
}

.form-actions button[type="button"]:hover {
  background: var(--text-secondary);
}

.error-message {
  background: #f44336;
  color: white;
  padding: 15px;
  border-radius: 8px;
  margin-bottom: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.error-message button {
  background: none;
  border: none;
  color: white;
  font-size: 18px;
  cursor: pointer;
}

/* Responsive Design */
@media (max-width: 768px) {
  .profiles-content {
    grid-template-columns: 1fr;
  }

  .profiles-grid {
    grid-template-columns: 1fr;
  }

  .details-grid {
    grid-template-columns: 1fr;
  }

  .form-row {
    grid-template-columns: 1fr;
  }
}

/* Forum Styles */
.forum-container {
  padding: 24px;
  max-width: 1200px;
  margin: 0 auto;
  color: var(--text-primary);
}

.forum-header {
  text-align: center;
  margin-bottom: 48px;
  padding-bottom: 24px;
  border-bottom: 1px solid var(--border-color);
}

.forum-header h1 {
  font-size: 32px;
  font-weight: 700;
  margin-bottom: 12px;
  background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.forum-header p {
  font-size: 18px;
  color: var(--text-secondary);
  margin: 0;
}

.forum-content {
  display: flex;
  flex-direction: column;
  gap: 48px;
}

.forum-intro {
  text-align: center;
  background: var(--bg-secondary);
  padding: 32px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.forum-intro h2 {
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 16px;
  color: var(--text-primary);
}

.forum-intro p {
  font-size: 16px;
  color: var(--text-secondary);
  line-height: 1.6;
  margin: 0;
}

.forum-features {
  background: var(--bg-secondary);
  padding: 32px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.feature-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 24px;
}

.feature-item {
  text-align: center;
  padding: 24px;
  background: var(--bg-tertiary);
  border-radius: 8px;
  border: 1px solid var(--border-color);
  transition: all 0.2s ease;
}

.feature-item:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
}

.feature-icon {
  font-size: 32px;
  margin-bottom: 16px;
  display: block;
}

.feature-item h3 {
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 12px;
  color: var(--text-primary);
}

.feature-item p {
  font-size: 14px;
  color: var(--text-secondary);
  line-height: 1.5;
  margin: 0;
}

.forum-access {
  text-align: center;
  background: var(--bg-secondary);
  padding: 48px 32px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.forum-button {
  background: linear-gradient(135deg, var(--accent-primary), var(--accent-secondary));
  color: white;
  border: none;
  padding: 16px 32px;
  border-radius: 8px;
  font-size: 18px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
  text-decoration: none;
  display: inline-block;
  margin-bottom: 16px;
}

.forum-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
}

.forum-note {
  font-size: 14px;
  color: var(--text-secondary);
  margin: 0;
  font-style: italic;
}

.forum-loading {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 80px 32px;
  text-align: center;
}

.forum-loading p {
  margin-top: 16px;
  font-size: 16px;
  color: var(--text-secondary);
}

.forum-auth-required {
  text-align: center;
  background: var(--bg-secondary);
  padding: 48px 32px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.forum-auth-required h2 {
  font-size: 28px;
  font-weight: 600;
  margin-bottom: 16px;
  color: var(--text-primary);
}

.forum-auth-required > p {
  font-size: 16px;
  color: var(--text-secondary);
  margin-bottom: 32px;
}

.forum-features h3 {
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 16px;
  color: var(--text-primary);
}

.forum-features ul {
  list-style: none;
  padding: 0;
  margin: 0;
}

.forum-features li {
  padding: 8px 0;
  font-size: 16px;
  color: var(--text-secondary);
  position: relative;
  padding-left: 24px;
}

.forum-features li::before {
  content: "";
  position: absolute;
  left: 0;
  color: var(--accent-primary);
  font-weight: bold;
}

.forum-error {
  text-align: center;
  background: var(--bg-secondary);
  padding: 48px 32px;
  border-radius: 12px;
  border: 1px solid var(--border-color);
}

.forum-error h2 {
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 16px;
  color: #ef4444;
}

.forum-error p {
  font-size: 16px;
  color: var(--text-secondary);
  margin-bottom: 24px;
}

.retry-button {
  background: var(--accent-primary);
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 6px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.retry-button:hover {
  background: var(--accent-secondary);
  transform: translateY(-1px);
}

/* Responsive Forum Styles */
@media (max-width: 768px) {
  .forum-container {
    padding: 16px;
  }

  .forum-header h1 {
    font-size: 24px;
  }

  .forum-header p {
    font-size: 16px;
  }

  .forum-content {
    gap: 32px;
  }

  .forum-intro,
  .forum-features,
  .forum-access,
  .forum-auth-required,
  .forum-error {
    padding: 24px 16px;
  }

  .feature-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .forum-button {
    font-size: 16px;
    padding: 14px 28px;
  }

  .forum-intro h2 {
    font-size: 20px;
  }

  .forum-auth-required h2 {
    font-size: 24px;
  }
}

/* Checkout Page Styles */
