  background: var(--bg-secondary);
  border-radius: 8px;
  padding: 20px;
  margin: 20px 0;
  color: var(--text-primary);
  border: 1px solid var(--border-color);
}

.visualization-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
  flex-wrap: wrap;
  gap: 10px;
}

.visualization-header h3 {
  margin: 0;
  color: var(--text-primary);
  font-size: 1.5rem;
}

.view-mode-selector {
  display: flex;
  gap: 5px;
  background: var(--bg-tertiary);
  border-radius: 6px;
  padding: 4px;
}

.view-mode-selector button {
  background: transparent;
  border: none;
  padding: 8px 16px;
  border-radius: 4px;
  color: var(--text-secondary);
  cursor: pointer;
  transition: all 0.2s ease;
  font-size: 0.9rem;
}

.view-mode-selector button:hover {
  background: var(--bg-hover);
  color: var(--text-primary);
}

.view-mode-selector button.active {
  background: var(--primary-color);
  color: white;
}

.solution-metrics {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
  padding: 15px;
  background: var(--bg-tertiary);
  border-radius: 6px;
  border: 1px solid var(--border-color);
}

.metric {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
}

.metric-label {
  font-size: 0.8rem;
  color: var(--text-secondary);
  margin-bottom: 5px;
  font-weight: 500;
}

.metric-value {
  font-size: 1.1rem;
  font-weight: 600;
  color: var(--text-primary);
}

.visualization-content {
  min-height: 400px;
}

/* Strategy View Styles */
.strategy-view {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 20px;
  align-items: start;
}

.decision-points-container {
  background: var(--bg-tertiary);
  border-radius: 6px;
  padding: 15px;
  border: 1px solid var(--border-color);
}

.decision-points-container h4 {
  margin: 0 0 15px 0;
  color: var(--text-primary);
  font-size: 1.2rem;
}

.decision-category {
  margin-bottom: 20px;
}

.category-title {
  margin: 0 0 10px 0;
  color: var(--text-primary);
  font-size: 1rem;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 8px;
}

.decision-points-grid {
  display: grid;
  gap: 10px;
}

.decision-point-card {
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: 6px;
  padding: 12px;
  cursor: pointer;
  transition: all 0.2s ease;
  position: relative;
}

.decision-point-card:hover {
  border-color: var(--primary-color);
  transform: translateY(-1px);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.decision-point-card.selected {
  border-color: var(--primary-color);
  background: var(--bg-hover);
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.2);
}

.card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.position-badge {
  background: var(--primary-color);
  color: white;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 0.75rem;
  font-weight: 600;
}

.board-info {
  font-size: 0.8rem;
  color: var(--text-secondary);
  font-weight: 500;
}

.card-body {
  margin-bottom: 8px;
}

.situation-description {
  font-size: 0.9rem;
  color: var(--text-primary);
  line-height: 1.4;
  margin-bottom: 8px;
}

.action-history {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.8rem;
}

.history-label {
  color: var(--text-secondary);
  font-weight: 500;
}

.history-actions {
  display: flex;
  gap: 4px;
}

.history-action {
  background: var(--bg-secondary);
  color: var(--text-primary);
  padding: 2px 6px;
  border-radius: 4px;
  font-size: 0.75rem;
  font-weight: 500;
}

.card-strategy {
  border-top: 1px solid var(--border-color);
  padding-top: 8px;
  margin-top: 8px;
}

.strategy-label {
  font-size: 0.8rem;
  color: var(--text-secondary);
  font-weight: 600;
  margin-bottom: 6px;
}

.strategy-actions {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
}

.strategy-action {
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  padding: 4px 8px;
  display: flex;
  align-items: center;
  gap: 6px;
  font-size: 0.75rem;
}

.strategy-action .action-name {
  color: var(--text-primary);
  font-weight: 500;
}

.strategy-action .action-prob {
  background: var(--primary-color);
  color: white;
  padding: 2px 6px;
  border-radius: 10px;
  font-weight: 600;
  font-size: 0.7rem;
}

/* Strategy Details */
.strategy-details {
  background: var(--bg-tertiary);
  border-radius: 6px;
  padding: 20px;
  border: 1px solid var(--border-color);
}

.strategy-details h4 {
  margin: 0 0 15px 0;
  color: var(--text-primary);
  font-size: 1.2rem;
}

.situation-summary {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
  padding: 15px;
  background: var(--bg-primary);
  border-radius: 6px;
  border: 1px solid var(--border-color);
}

.summary-item {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.summary-label {
  font-size: 0.8rem;
  color: var(--text-secondary);
  font-weight: 500;
}

.summary-value {
  font-size: 0.9rem;
  color: var(--text-primary);
  font-weight: 600;
}

.actions-container {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.action-bar {
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: 6px;
  padding: 12px;
  transition: all 0.2s ease;
}

.action-bar.optimal {
  border-color: var(--success-color);
  background: rgba(34, 197, 94, 0.1);
}

.action-label {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.action-name {
  font-weight: 600;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.action-percentage {
  font-weight: 600;
  color: var(--primary-color);
  font-size: 0.9rem;
}

.action-probability-bar {
  height: 8px;
  background: var(--bg-secondary);
  border-radius: 4px;
  overflow: hidden;
}

.action-probability-fill {
  height: 100%;
  background: var(--primary-color);
  border-radius: 4px;
  transition: width 0.3s ease;
}

.action-bar.optimal .action-probability-fill {
  background: var(--success-color);
}

/* Equity View Styles */
.equity-view {
  background: var(--bg-tertiary);
  border-radius: 6px;
  padding: 20px;
  border: 1px solid var(--border-color);
}

.equity-view h4 {
  margin: 0 0 20px 0;
  color: var(--text-primary);
  font-size: 1.2rem;
}

.equity-breakdown {
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-bottom: 20px;
}

.equity-player {
  background: var(--bg-primary);
  border-radius: 6px;
  padding: 15px;
  border: 1px solid var(--border-color);
}

.equity-label {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
}

.player-name {
  font-weight: 600;
  color: var(--text-primary);
  font-size: 0.9rem;
}

.equity-percentage {
  font-weight: 600;
  color: var(--primary-color);
  font-size: 0.9rem;
}

.equity-bar {
  height: 12px;
  background: var(--bg-secondary);
  border-radius: 6px;
  overflow: hidden;
}

.equity-fill {
  height: 100%;
  background: linear-gradient(90deg, var(--primary-color), var(--primary-color-light));
  border-radius: 6px;
  transition: width 0.3s ease;
}

.equity-details {
  border-top: 1px solid var(--border-color);
  padding-top: 20px;
}

.equity-details h5 {
  margin: 0 0 15px 0;
  color: var(--text-primary);
  font-size: 1rem;
}

.breakdown-stats {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.stat-group {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px;
  background: var(--bg-primary);
  border-radius: 4px;
  border: 1px solid var(--border-color);
}

.stat-label {
  font-weight: 500;
  color: var(--text-secondary);
  font-size: 0.85rem;
}

.stat-value {
  font-weight: 600;
  color: var(--text-primary);
  font-size: 0.85rem;
}

/* EV View Styles */
.ev-view {
  background: var(--bg-tertiary);
  border-radius: 6px;
  padding: 20px;
  border: 1px solid var(--border-color);
}

.ev-view h4 {
  margin: 0 0 20px 0;
  color: var(--text-primary);
  font-size: 1.2rem;
}

.ev-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
}

.ev-stat {
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: 6px;
  padding: 15px;
  text-align: center;
}

.stat-label {
  font-size: 0.8rem;
  color: var(--text-secondary);
  margin-bottom: 8px;
  font-weight: 500;
}

.stat-value {
  font-size: 1.2rem;
  font-weight: 600;
  color: var(--text-primary);
}

.ev-analysis {
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: 6px;
  padding: 15px;
}

.analysis-item {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 8px 0;
  border-bottom: 1px solid var(--border-color);
}

.analysis-item:last-child {
  border-bottom: none;
}

.analysis-label {
  font-weight: 500;
  color: var(--text-secondary);
  font-size: 0.9rem;
}

.analysis-value {
  font-weight: 600;
  font-size: 0.9rem;
}

.analysis-value.positive {
  color: var(--success-color);
}

.analysis-value.negative {
  color: var(--error-color);
}

/* Empty States */
.empty-strategy,
.empty-equity {
  text-align: center;
  padding: 40px 20px;
  color: var(--text-secondary);
  font-size: 1rem;
}

/* Responsive Design */
@media (max-width: 768px) {
  .strategy-view {
    grid-template-columns: 1fr;
    gap: 15px;
  }

  .visualization-header {
    flex-direction: column;
    align-items: stretch;
  }

  .view-mode-selector {
    justify-content: center;
  }

  .solution-metrics {
    grid-template-columns: repeat(2, 1fr);
  }

  .situation-summary {
    grid-template-columns: 1fr;
  }

  .ev-stats {
    grid-template-columns: 1fr;
  }
}

/* Dark Mode Enhancements */
@media (prefers-color-scheme: dark) {
  .decision-point-card:hover {
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
  }

  .action-bar.optimal {
    background: rgba(34, 197, 94, 0.15);
  }

  .strategy-action .action-prob {
    background: var(--primary-color);
  }
