    margin-bottom: 16px;
    border: 1px solid #fecaca;
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.error-text {
    flex: 1;
}

.retry-btn {
    background: #3b82f6;
    color: white;
    border: none;
    padding: 6px 12px;
    border-radius: 4px;
    font-size: 12px;
    cursor: pointer;
    transition: background-color 0.2s ease;
    align-self: flex-start;
}

.retry-btn:hover {
    background: #2563eb;
}

.retry-btn:disabled {
    background: #9ca3af;
    cursor: not-allowed;
}

.retry-info {
    font-size: 11px;
    color: #7b7b7b;
    font-style: italic;
}

.no-jobs {
    text-align: center;
    padding: 20px;
    color: #6b7280;
}

.no-jobs p {
    margin: 0;
}

.jobs-list {
    display: flex;
    flex-direction: column;
    gap: 8px; /* Reduced gap for more compact layout */
}

.job-item {
    background: #f9fafb;
    border: 1px solid #e5e7eb;
    border-radius: 6px;
    padding: 10px; /* Reduced padding for more compact layout */
    transition: border-color 0.2s ease;
}

.job-item.active-job {
    border-left: 3px solid #3b82f6;
    background: #eff6ff;
}

.job-item.completed-job {
    opacity: 0.8;
    background: #f9fafb;
}

.job-item:hover {
    border-color: #d1d5db;
}

.job-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 6px; /* Reduced margin */
}

.job-type {
    font-weight: 500;
    color: #374151;
    font-size: 14px; /* Slightly smaller font */
}

.job-icon {
    font-size: 14px;
}

.status-badge {
    display: inline-flex;
    align-items: center;
    gap: 4px;
    color: white;
    padding: 2px 8px;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 500;
    text-transform: capitalize;
    cursor: help;
    transition: opacity 0.2s ease;
}

.status-badge.clickable {
    cursor: pointer;
}

.status-badge.clickable:hover {
    opacity: 0.8;
    transform: scale(1.02);
}

.status-badge:hover {
    opacity: 0.8;
}

.view-results-hint {
    font-size: 10px;
}

.job-details {
    display: flex;
    flex-direction: column;
    gap: 6px; /* Reduced gap */
}

.job-progress {
    display: flex;
    align-items: center;
    gap: 8px;
}

.progress-bar {
    flex: 1;
    height: 4px; /* Thinner progress bar */
    background: #e5e7eb;
    border-radius: 2px;
    overflow: hidden;
}

.progress-fill {
    height: 100%;
    transition: width 0.3s ease;
}

.progress-text {
    font-size: 11px; /* Smaller text */
    color: #6b7280;
    min-width: 30px;
    text-align: right;
}

.progress-message {
    font-size: 11px; /* Smaller text */
    color: #6b7280;
    font-style: italic;
}

.job-error {
    display: flex;
    align-items: flex-start;
    gap: 8px;
    background: #fef2f2;
    border: 1px solid #fecaca;
    border-radius: 4px;
    padding: 6px; /* Reduced padding */
}

.error-icon {
    font-size: 14px;
    color: #dc2626;
    flex-shrink: 0;
}

.error-details {
    flex: 1;
    font-size: 12px; /* Smaller text */
    color: #dc2626;
}

.error-details strong {
    font-weight: 600;
}

.job-meta {
    display: flex;
    flex-wrap: wrap;
    gap: 12px;
    font-size: 11px; /* Smaller text */
    color: #6b7280;
}

.job-meta span {
    white-space: nowrap;
}

.job-actions {
    display: flex;
    gap: 8px;
    margin-top: 4px;
}

.cancel-btn {
    background: #ef4444;
    color: white;
    border: none;
    padding: 4px 8px; /* Smaller button */
    border-radius: 4px;
    font-size: 11px;
    cursor: pointer;
    transition: background-color 0.2s ease;
}

.cancel-btn:hover {
    background: #dc2626;
}

.cancel-btn:disabled {
    background: #9ca3af;
    cursor: not-allowed;
}

.completed-jobs-section {
    margin-top: 12px;
}

.section-divider {
    text-align: center;
    margin: 8px 0;
    position: relative;
}

.section-divider::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 1px;
    background: #e5e7eb;
    z-index: 1;
}

.section-divider span {
    background: #ffffff;
    padding: 0 12px;
    font-size: 12px;
    color: #6b7280;
    font-weight: 500;
    position: relative;
    z-index: 2;
}

.credits-details {
    margin-top: 16px;
    padding-top: 12px;
    border-top: 1px solid #e5e7eb;
}

.subscription-tier {
    font-size: 12px; /* Smaller text */
    color: #6b7280;
    margin-bottom: 8px;
}

.usage-stats {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.usage-item {
    display: flex;
    justify-content: space-between;
    font-size: 11px; /* Smaller text */
    color: #6b7280;
}

.usage-item span:last-child {
    font-weight: 500;
    color: #374151;
}

/* Responsive adjustments */
@media (max-width: 640px) {
    .job-status-panel {
        padding: 12px;
        max-height: 300px; /* Smaller on mobile */
    }

    .panel-header {
        flex-direction: column;
        align-items: flex-start;
        gap: 8px;
    }

    .credits-info {
        align-self: stretch;
    }

    .job-header {
        flex-direction: column;
        align-items: flex-start;
        gap: 4px;
    }

    .job-meta {
        flex-direction: column;
        gap: 4px;
    }

    .progress-text {
        min-width: 25px;
    }
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
    .job-status-panel {
        background: #1f2937;
        border-color: #374151;
        color: #f9fafb;
    }

    .panel-header h3 {
        color: #f9fafb;
    }

    .panel-header:hover {
        background-color: #374151;
    }

    .job-item {
        background: #374151;
        border-color: #4b5563;
    }

    .job-item.active-job {
        background: #1e3a8a;
