    }

    .job-item:hover {
        border-color: #6b7280;
    }

    .job-type {
        color: #f9fafb;
    }

    .progress-bar {
        background: #4b5563;
    }

    .credits-details {
        border-top-color: #4b5563;
    }

    .subscription-tier {
        color: #d1d5db;
    }

    .usage-item span:last-child {
        color: #f9fafb;
    }

    .error-message {
        background: #7f1d1d;
        border-color: #991b1b;
        color: #fecaca;
    }

    .job-error {
