    }

    .error-details {
        color: #fecaca;
    }

    .error-details strong {
        color: #fecaca;
    }

    .section-divider::before {
        background: #4b5563;
    }

    .section-divider span {
        background: #1f2937;
        color: #9ca3af;
    }
