  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 10000;
  backdrop-filter: blur(4px);
}

.auth-error-modal {
  background: var(--background-color, #ffffff);
  border-radius: 12px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  max-width: 400px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  animation: modalSlideIn 0.3s ease-out;
}

@keyframes modalSlideIn {
  from {
    opacity: 0;
    transform: translateY(-20px) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

.auth-error-modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 24px 16px;
  border-bottom: 1px solid var(--border-color, #e0e0e0);
}

.auth-error-modal-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--text-color, #333);
}

.close-button {
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: var(--text-muted, #666);
  padding: 4px;
  border-radius: 4px;
  transition: all 0.2s ease;
}

.close-button:hover {
  background: var(--hover-color, #f5f5f5);
  color: var(--text-color, #333);
}

.auth-error-modal-content {
  padding: 24px;
}

.auth-error-message {
  text-align: center;
  margin-bottom: 24px;
}

.auth-error-message p {
  margin: 0 0 8px 0;
  color: var(--text-color, #333);
  font-size: 1rem;
}

.auth-error-subtitle {
  color: var(--text-muted, #666) !important;
  font-size: 0.9rem !important;
}

.auth-error-login-form {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 6px;
}

.form-group label {
  font-weight: 500;
  color: var(--text-color, #333);
  font-size: 0.9rem;
}

.form-group input {
  padding: 12px 16px;
  border: 2px solid var(--border-color, #e0e0e0);
  border-radius: 8px;
  font-size: 1rem;
  transition: border-color 0.2s ease;
  background: var(--input-background, #ffffff);
  color: var(--text-color, #333);
}

.form-group input:focus {
  outline: none;
  border-color: var(--primary-color, #007bff);
  box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.1);
}

.login-error {
  background: var(--error-background, #fee);
  color: var(--error-color, #d32f2f);
  padding: 12px 16px;
  border-radius: 8px;
  font-size: 0.9rem;
  border: 1px solid var(--error-border, #fcc);
}

.auth-error-modal-actions {
  display: flex;
  gap: 12px;
  margin-top: 8px;
}

.auth-error-modal-actions .btn {
  flex: 1;
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
}

.btn-primary {
  background: var(--primary-color, #007bff);
  color: white;
}

.btn-primary:hover:not(:disabled) {
  background: var(--primary-hover, #0056b3);
  transform: translateY(-1px);
}

.btn-primary:disabled {
  background: var(--disabled-color, #ccc);
  cursor: not-allowed;
  transform: none;
}

.btn-secondary {
  background: var(--secondary-color, #6c757d);
  color: white;
}

.btn-secondary:hover {
  background: var(--secondary-hover, #545b62);
  transform: translateY(-1px);
}

/* Dark theme support */
@media (prefers-color-scheme: dark) {
  .auth-error-modal {
    --background-color: #2d2d2d;
    --text-color: #ffffff;
    --text-muted: #b0b0b0;
    --border-color: #404040;
    --input-background: #404040;
    --hover-color: #404040;
    --error-background: #3d1f1f;
    --error-color: #ff6b6b;
    --error-border: #5d2f2f;
  }
}

