.hand-breakdown-chart {
  margin: 1rem 0;
  padding: 1rem;
  background: var(--card-bg);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  color: var(--text-primary);
  max-width: 100%;
  overflow-x: hidden;
}

.breakdown-chart-title {
  margin: 0 0 1rem 0;
  font-size: 1.1rem;
  font-weight: 600;
  color: var(--text-primary);
}

.hand-breakdown-chart-empty {
  text-align: center;
  padding: 2rem;
  color: var(--text-secondary);
  font-style: italic;
}

.breakdown-chart-container {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  max-width: 100%;
}

.breakdown-chart-row {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 0.5rem 0;
  min-width: 0; /* Allow flex items to shrink below content size */
}

.hand-name {
  min-width: 100px;
  max-width: 120px;
  flex-shrink: 0;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.hand-label {
  font-weight: 600;
  color: var(--text-primary);
  font-size: 0.9rem;
  word-wrap: break-word;
  overflow-wrap: break-word;
}

.hand-frequency {
  font-size: 0.75rem;
  color: var(--text-secondary);
  margin-top: 0.125rem;
}

.percentage-bar-container {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
  min-width: 0; /* Allow flex items to shrink below content size */
  max-width: 100%;
}

.percentage-bar {
  height: 24px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 4px;
  display: flex;
  overflow: hidden;
  position: relative;
  width: 100%;
  min-width: 0; /* Allow flex items to shrink below content size */
}

.bar-segment {
  height: 100%;
  transition: all 0.3s ease;
  cursor: pointer;
  position: relative;
  min-width: 0; /* Allow flex items to shrink below content size */
}

.win-segment {
  background: linear-gradient(135deg, #4CAF50, #45a049);
  border-right: 1px solid rgba(255, 255, 255, 0.2);
}

.tie-segment {
  background: linear-gradient(135deg, #9E9E9E, #757575);
  border-right: 1px solid rgba(255, 255, 255, 0.2);
}

.loss-segment {
  background: linear-gradient(135deg, #f44336, #d32f2f);
}

.bar-segment:hover {
  opacity: 0.8;
  transform: scaleY(1.1);
}

.percentage-labels {
  display: flex;
  gap: 0.5rem;
  font-size: 0.75rem;
  font-weight: 600;
  margin-top: 0.25rem;
  flex-wrap: wrap;
  max-width: 100%;
}

.win-label {
  color: #4CAF50;
}

.tie-label {
  color: #9E9E9E;
}

.loss-label {
  color: #f44336;
}

.chart-legend {
  display: flex;
  gap: 1rem;
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid var(--border-color);
  justify-content: center;
  flex-wrap: wrap;
}

.legend-item {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.85rem;
  color: var(--text-secondary);
}

.legend-color {
  width: 16px;
  height: 16px;
  border-radius: 3px;
  border: 1px solid var(--border-color);
}

.win-color {
  background: linear-gradient(135deg, #4CAF50, #45a049);
}

.tie-color {
  background: linear-gradient(135deg, #9E9E9E, #757575);
}

.loss-color {
  background: linear-gradient(135deg, #f44336, #d32f2f);
}

/* Dark mode adjustments */
.dark-theme .percentage-bar {
  background: var(--bg-primary);
  border-color: var(--border-color);
}

.dark-theme .bar-segment {
  border-right-color: rgba(255, 255, 255, 0.1);
}

.dark-theme .win-segment {
  background: linear-gradient(135deg, #4CAF50, #45a049);
}

.dark-theme .tie-segment {
  background: linear-gradient(135deg, #BDBDBD, #9E9E9E);
}

.dark-theme .loss-segment {
  background: linear-gradient(135deg, #f44336, #d32f2f);
}

.dark-theme .win-label {
  color: #66BB6A;
}

.dark-theme .tie-label {
  color: #BDBDBD;
}

.dark-theme .loss-label {
  color: #EF5350;
}

/* Responsive design */
@media (max-width: 768px) {
  .breakdown-chart-row {
    flex-direction: column;
