  min-height: 100vh;
  background-color: var(--bg-primary);
  color: var(--text-primary);
  padding: 2rem 0;
}

.app-home-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.app-home-header {
  text-align: center;
  margin-bottom: 3rem;
}

.app-home-header h1 {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.app-home-header p {
  font-size: 1.2rem;
  color: var(--text-secondary);
  max-width: 600px;
  margin: 0 auto;
}

.modes-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 2rem;
  margin-bottom: 4rem;
}

.mode-card {
  background-color: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: 1rem;
  padding: 2rem;
  text-decoration: none;
  color: inherit;
  transition: all 0.3s ease;
  display: flex;
  flex-direction: column;
  position: relative;
  overflow: hidden;
}

.mode-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.15);
  border-color: var(--primary-color);
}

.mode-card:hover .mode-arrow {
  transform: translateX(4px);
}

.mode-icon {
  font-size: 3rem;
  margin-bottom: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 80px;
  height: 80px;
  background: var(--gradient-primary);
  border-radius: 1rem;
  margin-bottom: 1.5rem;
}

.mode-content {
  flex: 1;
}

.mode-content h3 {
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: var(--text-primary);
}

.mode-description {
  font-size: 1rem;
  color: var(--text-secondary);
  line-height: 1.6;
  margin-bottom: 1.5rem;
}

.mode-features {
  list-style: none;
  padding: 0;
  margin: 0;
}

.mode-features li {
  font-size: 0.9rem;
  color: var(--text-muted);
  padding: 0.25rem 0;
  position: relative;
  padding-left: 1.5rem;
}

.mode-features li:before {
  content: '';
  position: absolute;
  left: 0;
  color: var(--primary-color);
  font-weight: bold;
}

.mode-arrow {
  position: absolute;
  top: 2rem;
  right: 2rem;
  font-size: 1.5rem;
  color: var(--primary-color);
  transition: transform 0.3s ease;
}

.quick-stats {
  display: flex;
  justify-content: center;
  gap: 3rem;
  padding: 2rem 0;
  border-top: 1px solid var(--border-color);
}

.stat-card {
  text-align: center;
}

.stat-card .stat-number {
  display: block;
  font-size: 2rem;
  font-weight: 700;
  color: var(--primary-color);
  margin-bottom: 0.5rem;
  font-family: inherit;
  letter-spacing: -0.025em;
  line-height: 1;
}

.stat-card .stat-label {
  display: block;
  font-size: 1rem;
  color: var(--text-muted);
  font-family: inherit;
  font-weight: 500;
  line-height: 1.3;
}

/* Stamp Label Styles */
.mode-label-stamp {
  position: absolute;
  top: 15px;
  right: 15px;
  background: linear-gradient(45deg, #ff6b6b, #ee5a52);
  color: white;
  padding: 8px 16px;
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  border: 2px solid #ff6b6b;
  border-radius: 4px;
  transform: rotate(12deg);
  box-shadow: 
    0 2px 4px rgba(255, 107, 107, 0.3),
    inset 0 -1px 0 rgba(0, 0, 0, 0.2);
  z-index: 10;
  opacity: 0.9;
}

/* Special styling for Alpha Testing label */
.mode-card:has(.mode-label-stamp) .mode-label-stamp:contains("ALPHA TESTING") {
  background: linear-gradient(45deg, #4ecdc4, #44a08d);
  border-color: #4ecdc4;
  box-shadow: 
    0 2px 4px rgba(78, 205, 196, 0.3),
    inset 0 -1px 0 rgba(0, 0, 0, 0.2);
}

/* Alternative approach for alpha testing since :has() and :contains() might not be supported */
.mode-card .mode-label-stamp {
  background: linear-gradient(45deg, #ff6b6b, #ee5a52);
  border-color: #ff6b6b;
}

.mode-card[data-mode="solver"] .mode-label-stamp {
  background: linear-gradient(45deg, #4ecdc4, #44a08d);
  border-color: #4ecdc4;
  box-shadow: 
    0 2px 4px rgba(78, 205, 196, 0.3),
    inset 0 -1px 0 rgba(0, 0, 0, 0.2);
}

/* Hover effects for stamp */
.mode-card:hover .mode-label-stamp {
  transform: rotate(12deg) scale(1.05);
  opacity: 1;
}

/* Add rough paper texture effect */
.mode-label-stamp::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: 
    radial-gradient(circle at 20% 20%, rgba(255, 255, 255, 0.3) 1px, transparent 1px),
    radial-gradient(circle at 80% 80%, rgba(255, 255, 255, 0.2) 1px, transparent 1px),
    radial-gradient(circle at 60% 30%, rgba(0, 0, 0, 0.1) 1px, transparent 1px);
  pointer-events: none;
}

/* Responsive adjustments for stamps */
@media (max-width: 768px) {
  .mode-label-stamp {
    top: 10px;
    right: 10px;
    padding: 6px 12px;
    font-size: 0.7rem;
    transform: rotate(8deg);
  }

  .mode-card:hover .mode-label-stamp {
    transform: rotate(8deg) scale(1.05);
  }
}

@media (max-width: 480px) {
  .mode-label-stamp {
    top: 8px;
    right: 8px;
    padding: 5px 10px;
    font-size: 0.65rem;
    transform: rotate(6deg);
  }

  .mode-card:hover .mode-label-stamp {
    transform: rotate(6deg) scale(1.05);
  }
}

/* Disabled Card Styles */
.mode-card-disabled {
  opacity: 0.6;
  cursor: not-allowed;
  pointer-events: none;
  position: relative;
}

.mode-card-disabled:hover {
  transform: none;
  box-shadow: none;
  border-color: var(--border-color);
}

.mode-card-disabled:hover .mode-arrow {
  transform: none;
}

.mode-card-disabled:hover .mode-label-stamp {
  transform: rotate(12deg);
  opacity: 0.9;
}

.mode-card-disabled .mode-icon {
  filter: grayscale(30%);
}

.mode-card-disabled .mode-content h3 {
  color: var(--text-secondary);
}

.mode-card-disabled .mode-description {
  color: var(--text-muted);
}

.mode-card-disabled .mode-features li {
  color: var(--text-muted);
}

.mode-card-disabled .mode-features li:before {
  color: var(--text-muted);
}

.mode-card-disabled .mode-arrow {
  color: var(--text-muted);
}

/* Responsive Design */
@media (max-width: 768px) {
  .app-home-container {
    padding: 0 1rem;
  }

  .app-home-header h1 {
    font-size: 2rem;
  }

  .app-home-header p {
    font-size: 1.1rem;
  }

  .modes-grid {
    grid-template-columns: 1fr;
    gap: 1.5rem;
  }

  .mode-card {
    padding: 1.5rem;
  }

  .mode-icon {
    width: 60px;
    height: 60px;
    font-size: 2rem;
  }

  .mode-content h3 {
    font-size: 1.25rem;
  }

  .mode-description {
    font-size: 0.95rem;
  }

  .quick-stats {
    flex-direction: column;
    gap: 1.5rem;
    align-items: center;
  }

  .stat-card .stat-number {
    font-size: 1.75rem;
  }
}

@media (max-width: 480px) {
  .app-home {
    padding: 1rem 0;
  }

  .app-home-header {
    margin-bottom: 2rem;
  }

  .app-home-header h1 {
    font-size: 1.75rem;
  }

  .modes-grid {
    gap: 1rem;
  }

  .mode-card {
    padding: 1rem;
  }

  .mode-arrow {
    top: 1rem;
    right: 1rem;
  }
